import { Container, Image } from 'react-bootstrap'
import rainy from '../assets/img/rainy.png'
import { useEffect, useState } from 'react'

function TopBar({ locationData }) {
  const [weather, setWeather] = useState(null)

  useEffect(() => {
    if (locationData && locationData.length > 0) {
      fetchWeather(locationData[0].lat, locationData[0].lon)
    }
  }, [locationData])

  const API_KEY = 'c0244df251672a4e585ee6e01739992d'
  const fetchWeather = (lat, lon) => {
    fetch(
      `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=${API_KEY}`
    )
      .then((response) => {
        if (response.ok) {
          return response.json()
        } else {
          throw new Error('Fetch error')
        }
      })
      .then((data) => {
        setWeather(data)
        console.log(data)
      })
      .catch((error) => {
        console.error('Fetch error', error)
      })
  }

  return (
    <Container className="top-bar d-flex justify-content-between">
      <h1>
        {locationData
          ? `${locationData[0].name},
          ${locationData[0].state}`
          : 'Not found'}
      </h1>
      <div className="d-flex gap-3">
        <div className="d-flex align-items-center">
          <Image src={rainy} thumbnail />
        </div>
        <div className="text-end">
          <h2>Weather</h2>
          <h4>Today</h4>
          <h4>Rain</h4>
        </div>
      </div>
    </Container>
  )
}

export default TopBar
