import { useState } from 'react'
import { Button, Container, Form, Nav, Navbar } from 'react-bootstrap'

function CustomNavbar() {
  const [searchValue, setSearchValue] = useState('')
  const [location, setLocation] = useState(null)

  const API_KEY = 'c0244df251672a4e585ee6e01739992d'
  const fetchLocation = () => {
    fetch(
      `http://api.openweathermap.org/geo/1.0/direct?q=${searchValue}&limit=5&appid=${API_KEY}`
    )
      .then((response) => {
        if (response.ok) {
          response.json()
        } else {
          throw new Error('Fetch error')
        }
      })
      .then((data) => {
        setLocation(data)
      })
      .catch((error) => {
        console.error('Fetch error', error)
      })
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    fetchLocation()
  }

  return (
    <Container>
      <Navbar className="justify-content-between">
        <Form className="d-flex">
          <Form.Control
            type="text"
            placeholder="Enter the location"
            value={searchValue}
            onChange={(e) => setSearchValue(e.target.value)}
            onSubmit={handleSubmit}
          />
          <Button className="search-btn" type="submit">
            Search
          </Button>
        </Form>
        <div className="ms-auto d-flex align-items-center gap-5">
          <Nav.Link href="#home">Home</Nav.Link>
          <Nav.Link href="#features">News</Nav.Link>
          <Nav.Link href="#pricing">Live</Nav.Link>
          <Navbar.Brand href="#home">
            <i className="bi bi-umbrella-fill"></i>
          </Navbar.Brand>
        </div>
      </Navbar>
    </Container>
  )
}

export default CustomNavbar
