import { useState } from 'react'
import { Button, Container, Form, Nav, Navbar } from 'react-bootstrap'
import TopBar from './TopBar'
import NoContent from './NoContent'
import NotFound from './NotFound'

function CustomNavbar() {
  const [searchValue, setSearchValue] = useState('')
  const [locationData, setLocationData] = useState(null)
  const [error, setError] = useState('')
  const [isLoading, setIsLoading] = useState(false)

  const API_KEY = 'c0244df251672a4e585ee6e01739992d'
  const fetchLocation = () => {
    if (searchValue.trim().length === 0) {
      setError('Please search a location by its name, state, or zip code.')
      setLocationData(null)
      return
    }
    setError('')
    setIsLoading(true)
    fetch(
      `http://api.openweathermap.org/geo/1.0/direct?q=${searchValue}&limit=5&appid=${API_KEY}`
    )
      .then((response) => {
        if (response.ok) {
          return response.json()
        } else {
          throw new Error('Fetch error')
        }
      })
      .then((data) => {
        setLocationData(data)
        console.log(data)
      })
      .catch((error) => {
        console.error('Fetch error', error)
      })
      .finally(() => {
        setIsLoading(false)
      })
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    fetchLocation()
  }

  return (
    <>
      <Container>
        <Navbar className="justify-content-between">
          <Form className="d-flex">
            <Form.Control
              id="searchForm"
              type="text"
              placeholder="Enter the location"
              value={searchValue}
              onChange={(e) => setSearchValue(e.target.value)}
            />
            <Button className="search-btn" type="submit" onClick={handleSubmit}>
              Search
            </Button>
          </Form>
          <div className="ms-auto d-flex align-items-center gap-5">
            <Nav.Link href="" className="selected">
              Home
            </Nav.Link>
            <Nav.Link href="">News</Nav.Link>
            <Nav.Link href="">Live</Nav.Link>
            <Navbar.Brand href="" style={{ color: '$dark', cursor: 'pointer' }}>
              <i className="bi bi-umbrella-fill"></i>
            </Navbar.Brand>
          </div>
        </Navbar>
      </Container>
      {!searchValue && !error && (
        <NotFound message="Search a location by its name, state code, or country code" />
      )}
      {error && <NoContent />}
      {!error && locationData && <TopBar locationData={locationData} />}
    </>
  )
}

export default CustomNavbar
